import React, { useState } from 'react';
import {
    Container,
    Typography,
    TextField,
    Button,
    CircularProgress,
    Alert,
    CssBaseline,
    Paper,
} from '@mui/material';
import { auth } from './firebase'; // Import the Firebase auth
import { signInWithEmailAndPassword } from 'firebase/auth';
import Cookies from 'js-cookie'; // Import the js-cookie library

const Login = ({ onLoginSuccess }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(false);
    const [success, setSuccess] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(null);
        setSuccess(false);
        setLoading(true);

        try {
            const userCredential = await signInWithEmailAndPassword(auth, email, password);
            setSuccess(true);
            setError(null);
            onLoginSuccess(); // Call the function to update login state

            // Store login information in cookies
            const loginTime = new Date().getTime();
            const sessionDuration = 30 * 60 * 1000; // 30 minutes in milliseconds
            Cookies.set('isLoggedIn', 'true', { expires: 1 / 48 }); // Set cookie to expire in 30 minutes
            Cookies.set('loginTime', loginTime); // Store login time

            console.log('User logged in:', userCredential.user);
        } catch (err) {
            setError(err.message);
            setSuccess(false);
        } finally {
            setLoading(false);
        }
    };

    return (
        <Container component="main" maxWidth="xs">
            <CssBaseline />
            <Paper elevation={3} style={{ padding: '20px', marginTop: '50px' }}>
                <Typography variant="h5" align="center">
                    Login
                </Typography>
                <form onSubmit={handleSubmit} style={{ marginTop: '20px' }}>
                    <TextField
                        variant="outlined"
                        margin="normal"
                        required
                        fullWidth
                        label="Email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                    <TextField
                        variant="outlined"
                        margin="normal"
                        required
                        fullWidth
                        label="Password"
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    {loading ? (
                        <CircularProgress />
                    ) : (
                        <Button
                            type="submit"
                            fullWidth
                            variant="contained"
                            color="primary"
                            style={{ marginTop: '20px' }}
                        >
                            Login
                        </Button>
                    )}
                    {error && <Alert severity="error" style={{ marginTop: '20px' }}>{error}</Alert>}
                    {success && <Alert severity="success" style={{ marginTop: '20px' }}>Login successful!</Alert>}
                </form>
            </Paper>
        </Container>
    );
};

export default Login;
