import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
    Container,
    Typography,
    TextField,
    Button,
    List,
    ListItem,
    ListItemText,
    ListItemSecondaryAction,
    Snackbar,
    Alert
} from '@mui/material';

const LabelPrinter = () => {
    const [labelData, setLabelData] = useState([]);
    const [printerIp, setPrinterIp] = useState('');
    const [snackbarOpen, setSnackbarOpen] = useState(false);
    const [snackbarMessage, setSnackbarMessage] = useState('');

    useEffect(() => {
        const fetchLabelData = async () => {
            try {
                const response = await axios.get('http://127.0.0.1:5000/api/label_data');
                setLabelData(response.data);
            } catch (error) {
                console.error('Error fetching label data:', error);
            }
        };

        fetchLabelData();
    }, []);

    const printLabel = (item) => {
        const zpl = `
            ^XA
            ^PW400
            ^LL200
            ^FO10,10^A0N,30,30^FDItem Number: ${item.itemNum}^FS
            ^FO10,50^A0N,30,30^FDItem Name: ${item.itemName}^FS
            ^FO10,100^BY2,2,50
            ^BCN,N,N,N,N,D^FD${item.itemName}^FS
            ^FO10,170^A0N,25,25^FDPrice: $${item.price}^FS
            ^XZ
        `;

        const printUrl = `http://${printerIp}/zpl`;

        axios.post(printUrl, zpl, {
            headers: {
                'Content-Type': 'text/plain',
            },
        })
        .then(response => {
            setSnackbarMessage('Label sent to printer successfully!');
            setSnackbarOpen(true);
        })
        .catch(error => {
            console.error('Error sending label to printer:', error);
            setSnackbarMessage('Error sending label to printer.');
            setSnackbarOpen(true);
        });
    };

    const handleSnackbarClose = () => {
        setSnackbarOpen(false);
    };

    return (
        <Container maxWidth="sm" style={{ marginTop: '20px' }}>
            <Typography variant="h4" align="center" gutterBottom>
                Label Printer
            </Typography>
            <TextField
                label="Printer IP Address"
                variant="outlined"
                fullWidth
                value={printerIp}
                onChange={(e) => setPrinterIp(e.target.value)}
                style={{ marginBottom: '20px' }}
            />
            <List>
                {labelData.map(item => (
                    <ListItem key={item.itemNum}>
                        <ListItemText
                            primary={`${item.itemName} - $${item.price}`}
                            secondary={`Item Number: ${item.itemNum}`}
                        />
                        <ListItemSecondaryAction>
                            <Button 
                                variant="contained" 
                                color="primary" 
                                onClick={() => printLabel(item)}
                            >
                                Print Label
                            </Button>
                        </ListItemSecondaryAction>
                    </ListItem>
                ))}
            </List>
            <Snackbar open={snackbarOpen} autoHideDuration={6000} onClose={handleSnackbarClose}>
                <Alert onClose={handleSnackbarClose} severity="success" sx={{ width: '100%' }}>
                    {snackbarMessage}
                </Alert>
            </Snackbar>
        </Container>
    );
};

export default LabelPrinter;
