// src/App.js
import React, { useState } from 'react';
import './App.css';
import { BrowserRouter as Router, Route, Switch, Redirect } from 'react-router-dom';
import Login from './Login'; // Default import
import Inventory from './Inventory'; // Default import
import LabelPrinter from './LabelPrinter'; // Default import
import { Container } from '@mui/material';

function App() {
    const [isLoggedIn, setIsLoggedIn] = useState(false); // State to manage login status

    const handleLoginSuccess = () => {
        setIsLoggedIn(true); // Update login status on successful login
    };

    return (
        <Router>
            <Container>
                <Switch>
                    <Route path="/" exact>
                        <Login onLoginSuccess={handleLoginSuccess} />
                    </Route>
                    <Route path="/inventory">
                        {isLoggedIn ? <Inventory /> : <Redirect to="/" />}
                    </Route>
                    <Route path="/label-printer">
                        {isLoggedIn ? <LabelPrinter /> : <Redirect to="/" />}
                    </Route>
                </Switch>
            </Container>
        </Router>
    );
}

export default App;
